    def clickMethod(self):
        simbolos = ["letra", "letra", "simbolo", "letra", "digito", "simbolo"]
        sig_estado = 0
        cadena = self.lineEdit.text()
        self.tableWidget.setRowCount(len(cadena))
        for i in range(len(cadena)):
            act_estado = sig_estado
            clase = checkCharacter(cadena[i])
            sig_estado = checkTable(act_estado, clase)

            # Estado actual
            self.tableWidget.setItem(
                i, 0, QtWidgets.QTableWidgetItem(str(act_estado)))
            # Caracter
            self.tableWidget.setItem(
                i, 1, QtWidgets.QTableWidgetItem(cadena[i]))
            # Clase
            self.tableWidget.setItem(
                i, 2, QtWidgets.QTableWidgetItem(simbolos[clase]))
            # Estado siguiente
            self.tableWidget.setItem(
                i, 3, QtWidgets.QTableWidgetItem(str(sig_estado)))

        msg = "Cadena valida" if sig_estado != 5 else "Cadena no valida"

        self.label_3.setText(msg)

        if sig_estado != 5:
            if sig_estado == 1:
                self.label_4.setText("Es un identificador")
            elif sig_estado == 2:
                self.label_4.setText("Es una palabra reservada")
            elif sig_estado == 3:
                self.label_4.setText("Es un identificador")
            elif sig_estado == 4:
                self.label_4.setText("Es un numero")

    def deleteAll(self):
        self.tableWidget.setRowCount(0)
        self.label_3.setText("")
        self.label_4.setText("")
